def save_json(repo, data):
    # Add fake IDs
    for i, item in enumerate(data):
        item["id"] = str(i + 1)
        
    path = os.path.join(BASE_DIR, repo, "data", "database.json")
    with open(path, "w", encoding="utf-8") as f:
        json.dump(data, f, indent=4)

